public class TempCode { public static long sumOfElements(int[] arr) { long sum = 0; for (int num : arr) { sum += num; } return sum; } public static void main(String[] args) { Scanner scanner = new Scanner(System.in); int n = scanner.nextInt(); int[] arr = new int[n]; for (int i = 0; i < n; i++) { arr[i] = scanner.nextInt(); } long result = sumOfElements(arr); System.out.println(result); scanner.close(); } }